// src/pages/HomePage.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
const HomePage = () => {
  const navigate = useNavigate();

  const cuisines = [
    { name: 'Italian', image: 'italian.jpg' },
    { name: 'Chinese', image: 'chinese.jpg' },
    { name: 'North Indian', image: 'north_indian.jpg' },
    { name: 'South Indian', image: 'south_indian.jpg' },
    { name: 'Korean', image: 'korean.jpg' },
    // Add more cuisines as needed
  ];

  const [user, setUser] = useState(null);

  // Fetch the user if authenticated (optional)
  useEffect(() => {
    axios.get('/api/auth/status')
      .then((response) => {
        setUser(response.data.user);
      })
      .catch((error) => {
        console.log('Error fetching user:', error);
      });
  }, []);

  const handleCuisineClick = (cuisine) => {
    navigate(`/cuisine/${cuisine}`);
  };

  return (
    <div>
      <h1>Welcome to the Restaurant, {user ? user.displayName : 'Guest'}</h1>
      <p>Select a cuisine to explore our menu.</p>
      <div style={{ display: 'flex', justifyContent: 'space-around', flexWrap: 'wrap' }}>
        {cuisines.map((cuisine) => (
          <div key={cuisine.name} onClick={() => handleCuisineClick(cuisine.name)} style={{ cursor: 'pointer', textAlign: 'center' }}>
            <h2>{cuisine.name}</h2>
            <img src={cuisine.image} alt={cuisine.name} style={{ width: '150px', height: '150px' }} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
