// src/pages/CuisinePage.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Cuisine = () => {
  const { cuisine } = useParams(); // Get the cuisine from the URL
  const [foodItems, setFoodItems] = useState([]);

  // Fetch food items for the selected cuisine
  useEffect(() => {
    axios.get(`/api/cuisine/${cuisine}`)
      .then((response) => {
        setFoodItems(response.data);
      })
      .catch((error) => {
        console.log('Error fetching food items:', error);
      });
  }, [cuisine]);

  const handleAddToCart = (foodItem) => {
    // Handle the add to cart functionality
    console.log(`Added ${foodItem.name} to cart`);
  };

  const handleIncreaseQuantity = (foodItem) => {
    // Increase the quantity of the item
    console.log(`Increased quantity of ${foodItem.name}`);
  };

  const handleDecreaseQuantity = (foodItem) => {
    // Decrease the quantity of the item
    console.log(`Decreased quantity of ${foodItem.name}`);
  };

  return (
    <div>
      <h1>{cuisine} Cuisine</h1>
      <div>
        {foodItems.map((foodItem) => (
          <div key={foodItem._id} style={{ marginBottom: '20px' }}>
            <h2>{foodItem.name}</h2>
            <img src={foodItem.imageUrl} alt={foodItem.name} style={{ width: '200px', height: '200px' }} />
            <p>Price: ${foodItem.price}</p>
            <button onClick={() => handleAddToCart(foodItem)}>Add to Cart</button>
            <div>
              <button onClick={() => handleDecreaseQuantity(foodItem)}>-</button>
              <span> Quantity: {/* Display quantity here */} 1 </span>
              <button onClick={() => handleIncreaseQuantity(foodItem)}>+</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Cuisine;
