// src/pages/CuisinePage.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Cuisine = () => {
  const { cuisineId } = useParams();  // Getting the cuisineId from URL
  const [foodItems, setFoodItems] = useState([]);

  useEffect(() => {
    // Fetch the food items based on the cuisineId
    const fetchFoodItems = async () => {
      try {
        const response = await axios.get(`/api/cuisines/${cuisineId}/foods`);
        setFoodItems(response.data);
      } catch (error) {
        console.error('Error fetching food items:', error);
      }
    };

    fetchFoodItems();
  }, [cuisineId]);

  return (
    <div className="cuisine-page">
      <h2>{cuisineId} Cuisine</h2>
      <div className="food-list">
        {foodItems.map((food) => (
          <div key={food._id} className="food-item">
            <img src={food.imageUrl} alt={food.name} />
            <h3>{food.name}</h3>
            <p>Price: ${food.price}</p>
            <button>Add to Cart</button>
            <QuantityButton />
          </div>
        ))}
      </div>
    </div>
  );
};

const QuantityButton = () => {
  const [quantity, setQuantity] = useState(1);

  const increment = () => setQuantity(quantity + 1);
  const decrement = () => setQuantity(quantity > 1 ? quantity - 1 : 1);

  return (
    <div className="quantity-button">
      <button onClick={decrement}>-</button>
      <span>{quantity}</span>
      <button onClick={increment}>+</button>
    </div>
  );
};

export default CuisinePage;
