// src/pages/HomePage.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axios from 'axios';

const HomePage = () => {
  const navigate = useNavigate();
  const cuisines = [
    { name: 'Italian', image: 'italian.jpg' },
    { name: 'Chinese', image: 'chinese.jpg' },
    // add more cuisines
  ];
  const [user, setUser] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/auth/status', { withCredentials: true })
      .then(response => setUser(response.data))
      .catch(() => window.location.href = '/');
  }, []);

  const handleCuisineClick = (cuisine) => {
    navigate(`/cuisine/${cuisine}`);
  };

  return (
    <div>
      <h1>Welcome to the Restaurant, {user ? user.displayName : 'Guest'}</h1>
      <p>Select a cuisine to explore our menu.</p>
      <div>
        {cuisines.map((cuisine) => (
          <div key={cuisine.name} onClick={() => handleCuisineClick(cuisine.name)}>
            <h2>{cuisine.name}</h2>
            <img src={cuisine.image} alt={cuisine.name} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
